package com.github.salpadding.rlpstream;

import lombok.SneakyThrows;
import org.apache.commons.codec.binary.Hex;

/**
 * encode 1000000 times 0.003976 ms avg
 * decode 1000000 times 0.002533 ms avg
 */
public class Bench {
    public static void main(String[] args) {
        benchEncodeDecode();
    }

    @SneakyThrows
    public static void benchEncodeDecode() {
        String dt = "";
        byte[] bytes = Hex.decodeHex(dt);

        RlpTest.PoolData[] datas = Rlp.decode(bytes, RlpTest.PoolData[].class);

        int count = 1000000;

        long now = System.currentTimeMillis();

        for(int i = 0; i < count; i++) {
            RlpWriter.encode(datas);
        }

        long end = System.currentTimeMillis();


        System.out.println("encode " + count + " times " + ((end - now) * 1.0 / count) + " ms avg ops = " + (count * 1000.0 / (end - now)));

        now = System.currentTimeMillis();

        for(int i = 0; i < count; i++) {
            Rlp.decode(bytes, RlpTest.PoolData[].class);
        }

        end = System.currentTimeMillis();

        System.out.println("decode " + count + " times " + ((end - now) * 1.0 / count) + " ms avg ops = " + (count * 1000.0 / (end - now)));
    }

}
